var Color4, GUI, Legend;

GUI = require('babylonjs-gui');

Color4 = require('babylonjs').Color4;

Legend = (function() {
    function Legend(ui) {
        this.ui = ui;
        this.panel = new GUI.StackPanel;
        this.panel.width = "200px";
        this.panel.fontSize = "24px";
        this.panel.background = "rgba(18,18,18,0.75)";
        this.panel.horizontalAlignment = GUI.Control.HORIZONTAL_ALIGNMENT_CENTER;
        this.panel.verticalAlignment = GUI.Control.VERTICAL_ALIGNMENT_BOTTOM;
        this.ui.addControl(this.panel);
        this.header = new GUI.TextBlock;
        this.header.text = "Legend";
        this.header.fontFamily = 'fontMono';
        this.header.paddingLeft = "10px";
        this.header.paddingBottom = "10px";
        this.header.height = "40px";
        this.header.color = "white";
        this.header.textHorizontalAlignment = GUI.Control.HORIZONTAL_ALIGNMENT_CENTER;
        this.header.paddingTop = "10px";
        this.panel.addControl(this.header);
        this.hide();
    }

    Legend.prototype.show = function(mesh) {
        if (!mesh) {
            return this.hide();
        }
        this.header.text = mesh.name;
        return this.panel.isVisible = true;
    };

    Legend.prototype.hide = function() {
        return this.panel.isVisible = false;
    };

    return Legend;

})();

module.exports = Legend;

